function PAIcheckBox(id, checked, pathToReqURL = "") {
    var txt = new String("");
    if (checked)
        txt = "checked";

    var received = function (response) {
    };

    var failed = function (response) {
        alert("Error:  The database was not updated due to a network error.  id="
            + id + " " + txt);
    };

    Ext.Ajax.request({
        // Send the JSON object
        url: pathToReqURL + 'updateTestDataUseForPAI.php?action=checkbox&key=' + id
            + '&checked=' + checked,
        success: received,
        failure: failed
    });
}
